import {all, call,takeLatest, put} from 'redux-saga/effects';
import UserActionTypes from '../user/user.types';
import {clearCart} from './cart.actions';
import {selectCurrentUser} from '../user/user.selectors';


export function* clearCartOnSignOut(){
    yield put(clearCart());
}

export function* getFirebaseCart(){
    const user = yield selectCurrentUser();
    yield console.log("El usuario obtenido es : ",user);
}

export function* onSignInSuccess(){
    yield takeLatest(UserActionTypes.SIGN_IN_SUCCESS,getFirebaseCart);
}

export function* onSignOutSuccess(){
    yield takeLatest(UserActionTypes.SIGN_OUT_SUCCESS,clearCartOnSignOut);
}


export function* cartSagas() {
    yield(all([call(onSignOutSuccess),call(onSignInSuccess)]));
}
