import React from "react";
import { Route } from "react-router-dom";
import {createStructuredSelector} from "reselect";
import CollectionsOverview from "../../components/collections-overview/collections-overview.component";
import CollectionPage from "../collection/collection.component";
import {connect} from "react-redux";
import {fetchCollectionsStartAsync} from "../../redux/shop/shop.actions";
import withSpinner from "../../components/with-spinner/with-spinner.component";
import {selectIsCollectionFetching,selectIsCollectionLoaded} from "../../redux/shop/shop.selectors";

class ShopPage extends React.Component{


  componentDidMount() {
    const {fetchCollectionsStartAsync} = this.props;
    fetchCollectionsStartAsync();

  }


  render(){
      const {match,isCollectionLoaded} = this.props;
      const CollectionsOverviewSpinner = withSpinner(CollectionsOverview);
      const CollectionPageSpinner = withSpinner(CollectionPage);

    return (
      <div>
        <Route exact path={`${match.path}`} render={props=>{

          return <CollectionsOverviewSpinner isLoading={!isCollectionLoaded} otherProps={props} />;}} />
        <Route path={`${match.path}/:collectionId`} render={props=>{

          return <CollectionPageSpinner isLoading={!isCollectionLoaded} otherProps={props} />;}} />
      </div>
    );
  };

  }

  const mapStateToProps = createStructuredSelector({
    isCollectionFetching : selectIsCollectionFetching,
    selectIsCollectionLoaded : selectIsCollectionLoaded
  })

  const mapDispatchToProps = dispatch =>({
    fetchCollectionsStartAsync : () => dispatch(fetchCollectionsStartAsync())
  })


export default connect(mapStateToProps,mapDispatchToProps)(ShopPage);
