export const convertCollectionsSnapshotToMap= (collections)=>{
  console.log("Estoy llegando aqui con : ");
  console.log(collections); 

  const TransformedCollection = collections.docs.map(
    doc => {
      const {title,items} = doc.data();
      return {
        routeName : encodeURI(title.toLowerCase()),
        id : doc.id,
        title,
        items
      }
    }
  )

  return TransformedCollection.reduce((acumulator,collection)=>{
    acumulator[collection.title.toLowerCase()]=collection;
      return acumulator;
  },{});


}

firebase.initializeApp(config);
export const auth = firebase.auth();
//Sign in with google...
export const firestore = firebase.firestore();
export const googleProvider = new firebase.auth.GoogleAuthProvider();
googleProvider.setCustomParameters({ prompt: "select_account" });
export const signInWithGoogle = () => auth.signInWithPopup(googleProvider);

export const getCurrentUser = () => {
  return new Promise((resolve, reject) => {
    const unsubscribe = auth.onAuthStateChanged(userAuth => {
      unsubscribe();
      resolve(userAuth);
    }, reject);
  });
};

export const addCart = () =>{
  //Arreglo de usuarios
  const usersArray = [];
  //De esta manera recorro los usuarios en la coleccio贸n 
  firestore.collection('users')
  .get()
  .then(function(querySnapshot){
    querySnapshot.forEach(function(doc){
      const {displayName,createAt,email} = doc.data();
      usersArray.push({id: doc.id,cart : null})
    })
  });
  console.log("El arreglo es : ",usersArray);
  usersArray.forEach(obj=>{
    console.log("todo sale bien :",obj);
  })
  writeUserData(usersArray);
  //console.log("el resultado del script es : ",aux);

}

async function writeUserData(objectsToAdd){
  console.log("Estoy recibiendo como arreglo : ",objectsToAdd);
  const collectionRef = firestore.collection("carts");
  const batch = firestore.batch();


  const newDocRef = collectionRef.doc();
  //quiero insertar un nuevo objeto en la colecci贸n 
  batch.set(newDocRef,{hola:"hola cipayo"});
  /*await batch.commit().then(result => {
    console.log('Transaction success!');
  }).catch(err => {
    console.log('Transaction failure:', err);
  });*/

  objectsToAdd.forEach(obj =>{
    console.log("Estoy queriendo insertar ",obj);
    const newDocRef = collectionRef.doc(obj.id);
    batch.set(newDocRef,obj);
  });
  await batch.commit().then(result => {
    console.log('Transaction success!');
  }).catch(err => {
    console.log('Transaction failure:', err);
  });
}


export default firebase;
